import React, { useState } from "react";
import styled from "styled-components";
import { lighten } from "polished";
import { FiChevronDown } from "react-icons/fi";
// import { CTA } from "../styles-lib/buttons";
// import { flexbox } from "../styles-lib/mixins";
import { Label } from "../styles-lib/tipography";
import { TLVBox } from "../styles-lib/containers";
import { LinkBtn } from "../styles-lib/button";
import * as c from "../styles-lib/colors";
import GuestsCounter from "./GuestsCounter";
import { updateLabel } from "./GuestsMenu.Logic";

const GuestMenu = () => {
  let [open, setOpen] = useState(false);
  let [label, setLabel] = useState("Guests");
  let [counters, setCounters] = useState({
    adults: 0,
    children: 0,
    infants: 0
  });

  return (
    <Wrapper>
      <Label>guests</Label>
      <Box open={open} onClick={() => setOpen(!open)}>
        <MainBtn>{label}</MainBtn>
        <ChevronDown open={open} />
      </Box>
      {open && (
        <DropDown>
          <GuestsCounter
            title="adults"
            count={counters.adults}
            cb={updateLabel(counters, setCounters, setLabel)}
          />
          <GuestsCounter
            title="children"
            sub="Ages 2-12"
            count={counters.children}
            cb={updateLabel(counters, setCounters, setLabel)}
          />
          <GuestsCounter
            title="infants"
            sub="Under 2"
            count={counters.infants}
            cb={updateLabel(counters, setCounters, setLabel)}
          />
          <ApplyBtn onClick={() => setOpen(false)}>Apply</ApplyBtn>
        </DropDown>
      )}
    </Wrapper>
  );
};
export default GuestMenu;

const Wrapper = styled(TLVBox)`
  position: relative;
`;

const Box = styled.div`
  display: flex;
  position: relative;
  width: 100%;
  border: 1px solid ${({ open }) => (open ? c.GREEN : c.LIGHT_GREY)};
  border-radius: 0.4rem;
  cursor: pointer;
`;

const MainBtn = styled.button`
  flex: 1;
  outline: none;
  border: none;
  height: 5rem;
  text-align: left;
  text-indent: 1rem;
  font-size: 1.8rem;
  font-weight: 300;
  color: ${lighten(0.2, c.GREY)};
  cursor: pointer;
  background-color: white;
  background: transparent;
`;

const ChevronDown = styled(FiChevronDown)`
  position: absolute;
  top: 50%;
  right: 1rem;
  transition: transform 350ms;
  transform: translateY(-50%) ${({ open }) => `rotate(${open ? 180 : 0}deg)`};
  color: ${c.GREY};
  font-size: 2rem;
`;

const DropDown = styled.div`
  /* border: red 2px solid; */
  background: white;
  position: absolute;
  width: 100%;
  top: 10rem;
  box-shadow: 0px 2rem 4.6rem -1rem rgba(26, 26, 29, 0.3);
  padding-bottom: 5.8rem;
  padding-top: 1.5rem;
  border-radius: 0.3rem;
`;

const ApplyBtn = styled(LinkBtn)`
  position: absolute;
  bottom: 2.5rem;
  right: 2.4rem;
`;
