import React from 'react'
import {ListGroup, Table, Button, ButtonGroup, Accordion, Card} from 'react-bootstrap'
import './Accettazione.css'

class Accettazione extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            apiResponse: [],
            email_prop: localStorage.getItem('email'),
            error: false,
            errorMessage: ''
        }
    }

    componentDidMount() {
        const data = {
            email: this.state.email_prop
          };

          fetch('http://localhost:9000/getPrenotazioniAccettazione/prenotazioniAccettazione', {
              method: "POST",
              headers: {
                  'Content-type': 'application/json'
              },
              body: JSON.stringify(data)
          })
          .then((result) => result.text())
          .then((result) => {
            console.log(result);
            this.setState({ apiResponse: JSON.parse(result) });
      
            if(this.state.apiResponse.status === 'error') {
              this.setState({ error: true });
              this.setState({ errorMessage: this.state.apiResponse.message });
            }
        });
    }

    render() {
        if(this.state.apiResponse[0]) {
            var id = this.state.apiResponse[0].id_prenotazione;
            var ref_soggiornante = this.state.apiResponse[0].ref_soggiornante;
            var ref_cliente = this.state.apiResponse[0].ref_cliente;
            var ref_proprieta = this.state.apiResponse[0].ref_proprieta;
            var num_soggiornanti = this.state.apiResponse[0].num_soggiornanti;
            var costo = this.state.apiResponse[0].costo;
            var caparra = this.state.apiResponse[0].caparra;
        }
        return(
            <div className="accettazione">
                <div className="containerAccettazione">
                    <h1>Ecco le prenotazioni da accettare</h1>
                    <Accordion>
                        <div className="testa_accordo">
                        <p>ID</p>
                        <p>Cliente</p>
                        <p>Tempo rimanente</p>
                        <p>Struttura</p>
                        <p>Visualizza dettagli</p>
                        </div>
                        <div className="testa_accordo">
                        <p>{id}</p>
                        <p>{ref_cliente}</p>
                        <p> 5 minuti</p>
                        <p>{ref_proprieta}</p>
                        <Accordion.Toggle as={Button} variant="link" eventKey="0">visualizza dettagli</Accordion.Toggle>
                        </div>
                        <Accordion.Collapse eventKey="0">
                            <div className="corpo_accordo">
                                <p>{num_soggiornanti}</p>    
                            </div>                     
                        </Accordion.Collapse>
                        <div className="testa_accordo">
                        <p>1234</p>
                        <p>Giorgio Torton</p>
                        <p> 5 ore</p>
                        <p> Casa di Mana</p>
                        <Accordion.Toggle as={Button} variant="link" eventKey="1">visualizza dettagli</Accordion.Toggle>
                        </div>
                        <Accordion.Collapse eventKey="1">
                            <div className="corpo_accordo">
                                <p>Num soggiornanti ecc</p>    
                            </div>                     
                        </Accordion.Collapse>
                        <div className="testa_accordo">
                        <p>12345</p>
                        <p>Giorgio Lucia</p>
                        <p> 5 minuti</p>
                        <p> Casa di alfio</p>
                        <Accordion.Toggle as={Button} variant="link" eventKey="2">visualizza dettagli</Accordion.Toggle>
                        </div>
                        <Accordion.Collapse eventKey="2">
                            <div className="corpo_accordo">
                                <p>Num soggiornanti ecc</p>    
                            </div>                     
                        </Accordion.Collapse>
                   </Accordion>
    
                
                </div>
                <div className="listaAccettazioni">
                <h1>Ecco le prenotazioni accettate</h1>
                <Table className="tabella">
                    <thead>
                    <tr>
                        <th>ID </th>
                        <th>Cliente</th>
                        <th>Durata soggiorno</th>
                        <th>Struttura</th>
                    </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th>ID</th>
                            <th>Peppe tetto</th>
                            <th>15gg</th>
                            <th>Casa mia </th>
                        </tr>
                    </tbody>
                </Table>
                </div>
            </div>
        );
    }
}

export default Accettazione;
