/*CSS FATTO*/

import React from "react";
import camera from "../assets/camera.svg";   
import {Form} from "react-bootstrap"

import "../InserisciProp/InserimentoProprietÃ .css";

import { Redirect } from 'react-router-dom';

class ModificaStanza extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      dati_stanza: this.props.history.location.state ? this.props.history.location.state.dati_stanza : '',
      ref_bb: this.props.history.location.state ? this.props.history.location.state.dati_stanza.ref_bb : '',
      tipologia: this.props.history.location.state ? this.props.history.location.state.dati_stanza.tipologia : '',
      tariffa_stanza: this.props.history.location.state ? this.props.history.location.state.dati_stanza.tariffa_stanza : '',
      imgCV_path1SRC: this.props.history.location.state ? this.props.history.location.state.dati_stanza.imgCV_path1 : '',
      imgCV_path2SRC: this.props.history.location.state ? this.props.history.location.state.dati_stanza.imgCV_path2 : '',
      imgCV_path3SRC: this.props.history.location.state ? this.props.history.location.state.dati_stanza.imgCV_path3 : '',
      imgCV_path4SRC: this.props.history.location.state ? this.props.history.location.state.dati_stanza.imgCV_path4 : '',
      imgCV_path1: null,
      filename1: '',
      imgCV_path2: null,
      filename2: '',
      imgCV_path3: null,
      filename3: '',
      imgCV_path4: null,
      filename4: '',
      apiResponse: [],
      error: false,
      errorMessage: '',
      success: false
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeImg = (e) => {
    this.setState({ [e.target.name]: URL.createObjectURL(e.target.files[0]) });
    this.setState({ [e.target.id]: e.target.files[0] });
  }

  onSubmit = (e) => {
    const data1 = {
      nome_proprieta: this.state.nome_proprieta,
      indirizzo: this.state.indirizzo,
      localita: this.state.localita,
      provincia: this.state.provincia,
      tipo_proprieta: this.state.tipo_proprieta,
      servizi: this.state.servizi,
      ref_proprietario: this.state.ref_proprietario,
      descrizione: this.state.descrizione,
      id_proprieta: this.state.id_proprieta
    };

    fetch('http://localhost:9000/updateProprieta/fields', {
        method: 'POST',
        headers: {
          'Content-type':'application/json'
        },
        body: JSON.stringify(data1)
    })
    .then((result) => {result.text()})
    .then((result) => {
      this.setState({ apiResponse: result });

      if(this.state.apiResponse && this.state.apiResponse.status === 'error') {
        this.setState({
          error: true,
          errorMessage: this.state.apiResponse.message
        });
      }
      else {
        const data2 = {
          posti_letto: this.state.posti_letto,
          tariffa_casa: this.state.tariffa_casa,
          ref_proprieta_cv: this.state.ref_proprieta_cv,
        };

        this.setState({
          fileName1: data2.ref_proprieta_cv + "_1.jpg",
          fileName2: data2.ref_proprieta_cv + "_2.jpg",
          fileName3: data2.ref_proprieta_cv + "_3.jpg",
          fileName4: data2.ref_proprieta_cv + "_4.jpg",
        });
    
        fetch('http://localhost:9000/updateCasa/fields',{
            method: 'POST',
            headers: {
                'Content-type':'application/json'
            },
            body: JSON.stringify(data2)
        })
        .then((result) => result.text())
        .then((result) => {
          this.setState({ apiResponse: result });
    
          if(this.state.apiResponse && this.state.apiResponse.status === 'error') {
            this.setState({
              error: true,
              errorMessage: this.state.apiResponse.message
            });
          }
          else {
            var form = new FormData();
            if(this.state.imgCV_path1) {
              form.append("foto1", this.state.imgCV_path1, this.state.fileName1);
            }
            if(this.state.imgCV_path2) {
              form.append("foto2", this.state.imgCV_path2, this.state.fileName2);
            }
            if(this.state.imgCV_path3) {
              form.append("foto3", this.state.imgCV_path3, this.state.fileName3);
            }
            if(this.state.imgCV_path4) {
              form.append("foto4", this.state.imgCV_path4, this.state.fileName4);
            }

            fetch('http://localhost:9000/uploadFotoCV/upload', {
              method: "POST",
              body: form
            })
            .then((result) => result.text())
            .then((result) => {
              this.setState({ apiResponse: result });

            if(this.state.apiResponse.status === 'error') {
              this.setState({ error: true });
              this.setState({ errorMessage: this.state.apiResponse.message });
            }
            else {
              this.setState({ success: true });
            }
          });
          }
        });
      }
    });
  }

  render() {
    return (
        <div className="background">
      <div className="containerNew">  
        <div className="contentNew">
          <form onSubmit={handleSubmit}>
          <h2>Compila questo form per modificare la tua stanza!</h2>
            <label htmlFor="Nome">Nome</label>
            <input
              type="text"
              id="Nome"
              value={Nome}
              placeholder="Nome casa"
              onChange={e => setNome(e.target.value)}
              className="i"
            />

            <label htmlFor="Servizi">Servizi</label>
            <input
              type="text"
              id="Servizi"
              value={Servizi}
              placeholder="Elenco servizi"
              onChange={e => setServizi(e.target.value)}
              className="i"
            />
                <label htmlFor="Descrizione">Descrizione</label>
            <input
              type="text"
              id="Descrizione"
              value={Descrizione}
              placeholder="Descrizione"
              onChange={e => setDescrizione(e.target.value)}
              className="i"
            />
              <label htmlFor="Prezzo">Prezzo</label>
            <input
              type="text"
              id="Prezzo"
              value={Prezzo}
              placeholder="Prezzo"
              onChange={e => setPrezzo(e.target.value)}
              className="i"
            />

              <Form.Group controlId="PostiLetto">
              <Form.Label>PostiLetto</Form.Label>
              <Form.Control value={PostiLetto}  placeholder="Posti letto" as="select"   onChange={e => setPostiLetto(e.target.value)}  >
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
          
                </Form.Control>
            </Form.Group>

            <Form.Group>
              <label>Inserisci delle foto della tua struttura</label>
              <Form.Row className="justify-content-center">
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
            <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              </Form.Row>
              </Form.Group>
            
  
            <button type="submit" className="btn">
              Carica
            </button>
          </form>
        </div>
      </div>
      </div>
    );
  }
}

export default ModificaStanza;
