/*CSS FATTO*/

import React, { useState, useMemo } from "react";
import camera from "../assets/camera.svg";   
import {Form, Button} from "react-bootstrap"
import {Link} from "react-router-dom"

import "./InserimentoProprietÃ .css";

import { Redirect } from 'react-router-dom';


class InserimentoCasaVacanza extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      nome_proprieta: '',
      indirizzo: '',
      localita: '',
      provincia: '',
      tipo_proprieta: 'cv',
      servizi: '',
      descrizione: '',
      ref_proprietario: localStorage.getItem('email'),
      posti_letto:'',
      tariffa_casa: '',
      foto1: null,
      fileName1: '',
      foto2: null,
      fileName2: '',
      foto3: null,
      fileName3: '',
      foto4: null,
      fileName4: '',
      apiResponse: [],
      error: false,
      errorMessage: '',
      success: false
    };
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onChangeFoto = (e) => {
    this.setState({ [e.target.name]: e.target.files[0] });
  }

  onSubmitInsert = (e) => {
    e.preventDefault();

    const data1 = {
      nome_proprieta: this.state.nome_proprieta,
      indirizzo: this.state.indirizzo,
      localita: this.state.localita,
      provincia: this.state.provincia,
      tipo_proprieta: 'cv',
      servizi: this.state.servizi,
      ref_proprietario: this.state.ref_proprietario,
      descrizione: this.state.descrizione
    }

    fetch('http://localhost:9000/insertProprieta/new', {
      method: "POST",
      headers:{
        'Content-type': 'application/json'
      },
      body: JSON.stringify(data1)
    })
    .then((result) => result.text())
    .then((result) => {
      this.setState({ apiResponse: JSON.parse(result) });

      if(this.state.apiResponse.status === 'error') {
        this.setState({ error: true });
        this.setState({ errorMessage: this.state.apiResponse.message });
      }

      const data2 = {
        ref_proprieta_cv: this.state.apiResponse.insertId,
        posti_letto: this.state.posti_letto,
        tariffa_casa : this.state.tariffa_casa
      };

      this.setState({
        fileName1: data2.ref_proprieta_cv + "_1.jpg",
        fileName2: data2.ref_proprieta_cv + "_2.jpg",
        fileName3: data2.ref_proprieta_cv + "_3.jpg",
        fileName4: data2.ref_proprieta_cv + "_4.jpg",
      });

      fetch('http://localhost:9000/insertCasa/new',{
        method: "POST",
        headers: {
          'Content-type': 'application/json'
        },
        body: JSON.stringify(data2)
      })
      .then((result) => result.text())
      .then((result) => {
        this.setState({ apiResponse: result });

        if(this.state.apiResponse.status === 'error') {
          this.setState({ error: true });
          this.setState({ errorMessage: this.state.apiResponse.message });
        }
        else { 
          var form = new FormData();
          form.append("foto1", this.state.foto1, this.state.fileName1);
          form.append("foto2", this.state.foto2, this.state.fileName2);
          form.append("foto3", this.state.foto3, this.state.fileName3);
          form.append("foto4", this.state.foto4, this.state.fileName4);
      
          fetch('http://localhost:9000/uploadFotoCV/upload', {
              method: "POST",
              body: form
          })
          .then((result) => result.text())
          .then((result) => {
            this.setState({ apiResponse: result });

          if(this.state.apiResponse.status === 'error') {
            this.setState({ error: true });
            this.setState({ errorMessage: this.state.apiResponse.message });
          }
          else {
            this.setState({ success: true });
          }
        });
      }
    });
  });
  }

  render() {
    if(!localStorage.getItem('logged') || !localStorage.getItem('proprietario')) {
      return <Redirect
          to={{
              pathname: "/ErrorPage",
              state: { 
                error: true,
                errorMessage: "Utente non autorizzato" 
              }
          }}
      />
    }
    else if(this.state.error) {
      return <Redirect 
        to = {{
          pathname: "/ErrorPage",
          state: {
            error: true,
            errorMessage: this.state.errorMessage
          }
        }}
      />
    }
    else if(this.state.success) {
      return (
        <div className = "Errore">
          <h1>Inserimento avvenuto con successo!</h1>
          <p>La tua casa vacanza Ã¨ stata registrata correttamente all'interno del sistema</p>
        </div>
      );
    }
    else {
      return (
        <div className = "background">
            <div className = "containerNew">  
                <div className = "contentNew">
                    <form onSubmit = {this.onSubmitInsert}>
                    <h2>Compila questo form per registrare la tua casa vacanza!</h2>
                    <label htmlFor = "nome_proprieta">Nome</label>
              <input
                type = "text"
                id = "nome_proprieta"
                name = "nome_proprieta"
                placeholder = "Nome casa"
                onChange = {this.onChange}
                className = "i"
                required
              />
  
              <label htmlFor = "localita">CittÃ </label>
              <input
                type = "text"
                id = "localita"
                name = "localita"
                placeholder = "Nome cittÃ "
                onChange = {this.onChange}
                className = "i"
                required
              />
  
              <label htmlFor = "indirizzo">Via</label>
              <input
                type = "text"
                id = "indirizzo"
                name = "indirizzo"
                placeholder= "Indirizzo casa"
                onChange = {this.onChange}
                className = "i"
                required
              />
              
              <label htmlFor = "provincia">Provincia</label>
              <input
                type = "text"
                id = "provincia"
                name = "provincia"
                placeholder = "Provincia casa"
                onChange = {this.onChange}
                className = "i"
                required
              />
  
              <label htmlFor = "servizi">Servizi</label>
              <input
                type = "text"
                id = "servizi"
                name = "servizi"
                placeholder = "Elenco servizi"
                onChange = {this.onChange}
                className = "i"
                required
              />
                  <label htmlFor = "descrizione">Descrizione</label>
              <input
                type = "text"
                id = "descrizione"
                name = "descrizione"
                placeholder = "Descrizione casa"
                onChange = {this.onChange}
                className = "i"
                required
              />
                    <label htmlFor = "tariffa_casa">Tariffa</label>
                    <input
                        type = "text"
                        pattern = "^\d+(.\d{1,2})?$"
                        title = "Inserire un valore numerico usando un punto per i valori decimali"
                        id = "tariffa_casa"
                        name = "tariffa_casa"
                        placeholder = "Tariffa casa"
                        onChange = {this.onChange}
                        className = "i"
                    />
                    <label htmlFor = "posti_letto">Posti letto</label>
                    <input
                        type = "number"
                        id = "posti_letto"
                        name = "posti_letto"
                        min = "1"
                        max = "10"
                        placeholder = "1"
                        onChange = {this.onChange}
                        className = "i"
                        required
                    />
                    <Form.Group>
                        <label>Inserisci delle foto della tua struttura</label>
                        <Form.Row className = "justify-content-center">
                        <input
                        type = "file"
                        id = "foto1"
                        name = "foto1"
                        onChange = {this.onChangeFoto}
                        className = "inputImg"
                        accept = "image/*"
                        required
                        />
                        <input
                        type = "file"
                        id = "foto2"
                        name = "foto2"
                        onChange = {this.onChangeFoto}
                        className = "inputImg"
                        accept = "image/*"
                        required
                        />
                        <input
                        type = "file"
                        id = "foto3"
                        name = "foto3"
                        onChange = {this.onChangeFoto}
                        className = "inputImg"
                        accept = "image/*"
                        required
                        />
                        <input
                        type = "file"
                        id = "foto4"
                        name = "foto4"
                        onChange = {this.onChangeFoto}
                        className = "inputImg"
                        accept = "image/*"
                        required
                        />
                    </Form.Row>
                    </Form.Group>
                    <Link to="/InserimentoProprietÃ ">Torna indietro</Link>
                    <Button variant="primary" type="submit">
                      Carica
                    </Button>
                    </form>
                </div>
            </div>
        </div>
      );}
    }
}

export default InserimentoCasaVacanza;
