import React from 'react';
import { ReactiveBase, NumberBox, DateRange, RangeSlider} from '@appbaseio/reactivesearch';


//componenti barra di ricerca
function SearchBar() {
    return (
    <div className="container">
      
            <nav className="nav">
                <div className="title">airbeds</div>
            </nav>
            <div className="left-col">
            <Form inline>
      <FormControl type="text" placeholder="Search" className="mr-sm-2" />
      
    </Form>
                <DateRange
                    dataField="date_from"
                    componentId="DateRangeSensor"
                    title="When"
                    numberOfMonths={2}
                    queryFormat="basic_date"
                    initialMonth={new Date('04-01-2017')}
                />

                <NumberBox
                    componentId="GuestSensor"
                    dataField="accommodates"
                    title="Guests"
                    defaultSelected={2}
                    labelPosition="right"
                    data={{
                        start: 1,
                        end: 16,
                    }}
                />

                <RangeSlider
                    componentId="PriceSensor"
                    dataField="price"
                    title="Price Range"
                    range={{
                        start: 10,
                        end: 250,
                    }}
                    rangeLabels={{
                        start: '$10',
                        end: '$250',
                    }}
                    defaultSelected={{
                        start: 10,
                        end: 50,
                    }}
                    stepValue={10}
                    interval={20}
                    react={{
                        and: ['DateRangeSensor'],
                    }}
                    
                />
                <Button variant="outline-success">Search</Button>
            </div>
            </div>
);
                }
