/*CSS FATTO*/

import React, { useMemo } from "react";
import camera from "../assets/camera.svg";   
import {Form, Button} from "react-bootstrap"
import '../InserisciProp/InserimentoProprietÃ .css'



class ModificaCasaVacanza extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      dati_casa: this.props.history.location.state.dati_casa,
      id_proprieta: '',
      nome_proprieta: '',
      indirizzo: '',
      localita: '',
      provincia: '',
      tipo_proprieta : 'cv',
      servizi: '',
      descrizione: '',
      ref_proprieta_cv: '',
      posti_letto: '',
      tariffa_casa: '',
      apiResponse: [],
      error: false,
      errorMessage: '',
      success: false,
    }
  }

  componentDidMount() {
    this.setState({
      id_proprieta: this.state.dati_casa.ref_proprieta_cv,
      nome_proprieta: this.state.dati_casa.nome_proprieta,
      indirizzo: this.state.dati_casa.indirizzo,
      localita: this.state.dati_casa.localita,
      provincia: this.state.dati_casa.provincia,
      tipo_proprieta : 'cv',
      servizi: this.state.dati_casa.servizi,
      descrizione: this.state.dati_casa.descrizione,
      ref_proprieta_cv: this.state.dati_casa.ref_proprieta_cv,
      posti_letto: this.state.dati_casa.posti_letto,
      tariffa_casa: this.state.dati_casa.tariffa_casa
    });
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit = (e) => {
    const data = {
      email: this.state.email
    };

  fetch('http://localhost:9000/getDataInvio/dataInvio',{
      method: 'POST',
      headers: {
          'Content-type':'application/json'
      },
      body: JSON.stringify(data)
  })
  .then((result)=>result.text())
  .then((result)=>{
      this.setState({ apiResponse:JSON.parse(result) });

      if(this.state.apiResponse.status === 'error') {
          this.setState({
              error: true,
              errorMessage: this.state.apiResponse.message
          });
      }
    })
  }

  render() {

    var casa = this.state.dati_casa;

    return (
        <div className="background">
      <div className="containerNew"> 
        <div className="contentNew">
          <form onSubmit={this.onSubmit}>
          <h2>Modifica la tua casa vacanza con le informazioni che preferisci!</h2>
            <label htmlFor = "nome_proprieta">Nome</label>
            <input
              type = "text"
              id = "nome_proprieta"
              name = "nome_proprieta"
              defaultValue = {casa.nome_proprieta}
              onChange = {this.onChange}
              className = "i"
            />
            <label htmlFor = "localita">CittÃ </label>
            <input
              type = "text"
              id = "localita"
              name = "localita"
              defaultValue = {casa.localita}
              onChange = {this.onChange}
              className = "i"
            />
            <label htmlFor = "indirizzo">Indirizzo</label>
            <input
              type = "text"
              id = "indirizzo"
              name = "indirizzo"
              defaultValue = {casa.indirizzo}
              onChange = {this.onChange}
              className = "i"
            />
            
            <label htmlFor = "provincia">Provincia</label>
            <input
              type = "text"
              id = "provincia"
              name = "provincia"
              defaultValue = {casa.provincia}
              onChange = {this.onChange}
              className = "i"
            />

            <label htmlFor = "servizi">Servizi</label>
            <input
              type = "text"
              id = "servizi"
              name = "servizi"
              defaultValue = {casa.servizi}
              onChange = {this.onChange}
              className = "i"
            />
            <label htmlFor = "descrizione">Descrizione</label>
            <textarea 
              id = 'descrizione'
              name = 'descrizione'
              defaultValue = {casa.descrizione}
              onChange = {this.onChange}
              className = 'iTA'
            >
            </textarea>
        
              <Form.Group controlId = "posti_Letto" id = 'posti_letto' name = 'posti_letto' >
              <Form.Label>PostiLetto</Form.Label>
              <Form.Control 
                id = 'posti_letto'
                name = 'posti_letto'
                defaultValue = {casa.posti_letto}
                as = "select"   
                onChange = {this.onChange}  
              >
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                <option>6</option>
                <option>7</option>
                <option>8</option>
                </Form.Control>
            </Form.Group>
            
        
            <label htmlFor = "tariffa_casa">Prezzo</label>
            <input
              type = "text"
              id = "tariffa_casa"
              name = 'tariffa_casa'
              defaultValue = {casa.tariffa_casa}
              onChange = {this.onChange}
              className = "i"
            />
            <Form.Group>
              <label>Inserisci delle foto della tua struttura</label>
              <Form.Row className="justify-content-center">
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
            <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              <input
                type="file"
                className="inputImg"
                accept="image/*"
              />
              </Form.Row>
              </Form.Group>

              <Button variant="primary" type="submit">
              Carica
            </Button>
          </form>
        </div>
      </div>
      </div>
    );
  }
}

export default ModificaCasaVacanza;
