import React from 'react';
import {Link} from 'react-router-dom';





class SearchBar extends React.Component
{
  constructor(props)
  {
    super(props);

    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();

    today = yyyy + '-' + mm + '-' + dd;

    this.state = {
      type: 'text',
      minDateIn: today,
      minDateOut: today
    };

    this.onFocus = this.onFocus.bind(this);
    this.getMinDate = this.getMinDateIn.bind(this);
    this.setMinDate = this.setMinDateOut.bind(this);
    this.getMinDateOut = this.getMinDateOut.bind(this);
  }

  onFocus()
  {
    this.setState({type: 'date'});
  }

  getMinDateIn()
  {
    return this.state.minDateIn;
  }

  setMinDateOut(e)
  {
    this.setState({minDateOut: `${e}`});
  }

  getMinDateOut()
  {
    return this.state.minDateOut;
  }

  render()
  {
    return(
      <div className ="barra justify-content-left">
        <ul className = "generale">
          <li><input type = "text" 
            placeholder = "Destinazione" 
            id = "destinazione" 
            name = "destinazione" 
            className = "cercaDestinazione"></input></li>
          <li><div class="input-group input-daterange">
    <input type="text" class="form-control" value="2012-04-05"></input>
    <div class="input-group-addon">to</div>
    <input type="text" class="form-control" value="2012-04-19">
                 </input></div></li>
          <li><input type = "number" 
            placeholder = "N. Soggiornanti" 
            min = "1" max = "20" 
            id = "soggiornanti" 
            name = "soggiornanti" 
            className = "cercaSoggiornanti1"></input></li>
          <li><button type = "submit" className = "cercaBottone">CERCA</button></li>
        </ul>
      </div>
    );
  }
}

export default SearchBar;













