import React from 'react';
import {Link} from 'react-router-dom';





class SearchBar extends React.Component
{
  constructor(props)
  {
    super(props);

    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();

    today = yyyy + '-' + mm + '-' + dd;

    this.state = {
      type: 'text',
      minDateIn: today,
      minDateOut: today
    };

    this.onFocus = this.onFocus.bind(this);
    this.getMinDate = this.getMinDateIn.bind(this);
    this.setMinDate = this.setMinDateOut.bind(this);
    this.getMinDateOut = this.getMinDateOut.bind(this);
  }

  onFocus()
  {
    this.setState({type: 'date'});
  }

  getMinDateIn()
  {
    return this.state.minDateIn;
  }

  setMinDateOut(e)
  {
    this.setState({minDateOut: `${e}`});
  }

  getMinDateOut()
  {
    return this.state.minDateOut;
  }

  render()
  {
    return(
      <div className ="searchbar_div">
           <div id="searchbar">
               
           </div>
      </div>
    );
  }
}

export default SearchBar;













